INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES 
(1, 1, 0x323031362d30352d31362031373a32343a3437, 0x323031362d30352d31362030393a32343a3437, 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!', '世界，你好！', '', 'publish', 'open', 'open', '', 'hello-world', '', '', 0x323031362d30392d32362031363a31363a3432, 0x323031362d30392d32362030383a31363a3432, '', 0, 'http://mxy0226.comlu.com/?p=1', 0, 'post', '', 1),
(2, 1, 0x323031362d30352d31362031373a32343a3437, 0x323031362d30352d31362030393a32343a3437, 'This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://mxy0226.comlu.com/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!', '示例页面', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', 0x323031362d30352d31362031373a32343a3437, 0x323031362d30352d31362030393a32343a3437, '', 0, 'http://mxy0226.comlu.com/?page_id=2', 0, 'page', '', 0),
(8, 1, 0x323031362d30352d31362032313a31373a3031, 0x323031362d30352d31362031333a31373a3031, '<p>this page is tell about me:mxy</p><p>i am chiness(?) ; in shanghai. and very poor, has no money to eat ...:(</p>', 'this is about page', '', 'publish', 'closed', 'closed', '', 'this-is-about-page', '', '', 0x323031362d30352d31362032313a31373a3031, 0x323031362d30352d31362031333a31373a3031, '', 0, 'http://mxy0226.comlu.com/?page_id=8', 0, 'page', '', 0),
(10, 1, 0x323031362d30352d31362032313a31393a3038, 0x323031362d30352d31362031333a31393a3038, '<p>this is contact page, and will be added to say the work and life.</p><p>&nbsp;</p><p>that is ..</p>', 'contact page', '', 'publish', 'closed', 'closed', '', 'contact-page', '', '', 0x323031362d30352d31362032313a31393a3038, 0x323031362d30352d31362031333a31393a3038, '', 0, 'http://mxy0226.comlu.com/?page_id=10', 0, 'page', '', 0),
(13, 1, 0x323031362d30352d31362032313a32363a3430, 0x323031362d30352d31362031333a32363a3430, '', '关于我', '', 'publish', 'closed', 'closed', '', '13', '', '', 0x323031362d30362d32392030393a32313a3530, 0x323031362d30362d32392030313a32313a3530, '', 0, 'http://mxy0226.comlu.com/?p=13', 10, 'nav_menu_item', '', 0),
(15, 1, 0x323031362d30352d31362032323a32303a3137, 0x323031362d30352d31362031343a32303a3137, '年年月月，已经卅十多年了吧。。\r\n一心只想往前飞，\r\n但前是哪里？\r\n到了前后该怎么办？没想过，因为还没有到前呢。。。\r\n\r\n<!--more-->\r\n\r\n从进入吉电到现在已经50多天了，工资没涨，压力好大，同时对单片机有了了解，知道驱动怎么写（如果从头写的话，还是不行。不知道函数定义，怎么早GPIO口）但从头到现在还不如一个小我4岁的人了解系统（定时器，看门狗），硬件（电焊，电平高低，电路），驱动（时钟，按键延时）\r\n\r\n对上面的事该怎么办？不知道。\r\n\r\n以后该干什么？没想法，不知道，不了解，不讨论。\r\n1：不要再浪费时间了，小说，游戏视频（sc2,coh2）要少了，多写blog总结\r\n2：习惯养成，不要指望毅力\r\n3：做事干脆，不拖泥带水\r\n\r\n其他：自己能务实写，多些行动，少些教条；不要想的太完美，要不什么也不用做', '静下心来的一个blog', '这是关于这个博客的内容的想法。', 'publish', 'closed', 'closed', '', '01', '', '', 0x323031362d30362d32372031303a35343a3130, 0x323031362d30362d32372030323a35343a3130, '', 0, 'http://mxy0226.comlu.com/?p=15', 0, 'post', '', 0),
(17, 1, 0x323031362d30352d31362032323a32343a3238, 0x323031362d30352d31362031343a32343a3238, '', '首页', '', 'publish', 'closed', 'closed', '', '%e9%a6%96%e9%a1%b5', '', '', 0x323031362d30362d32392030393a32313a3530, 0x323031362d30362d32392030313a32313a3530, '', 0, 'http://mxy0226.comlu.com/?p=17', 1, 'nav_menu_item', '', 0),
